import './App.css';
import React, { useState } from 'react';
import Product from './Product'

const App = () => {
  const [input, setInput] = useState("");
  return (
    <div className="App">
      <textarea value={input.value} onChange={input => setInput(input)} />
      <p>{input.value}</p>
      <Product/>
    </div>
  );
}

export default App;
