import React, {useState} from 'react'
import Modal from "react-modal";

const Description = ({ product }) => {

  const [price, setPrice] = useState(product?.variants[0]?.price);
  const [isOpen, setIsOpen] = useState(false);

  const toggleModal = () => {
    setIsOpen(!isOpen);
  }

  return (
    <div style={{display: 'flex', justifyContent: 'space-between', flex: 1, flexDirection: 'column'}}>
        <h3 style={{color: 'red'}}>{product?.title}</h3>
        <div style={{alignSelf: 'center', flexDirection: 'row', display: 'flex', width: '100%', justifyContent: 'space-between'}}>
            <p style={{color: product?.status ? 'green' : 'red'}}>{product?.status ? 'Stokta Mevcut' : 'Stokta Bulunmuyor'}</p>
            <p style={{color: '#b30000'}}>{price}â‚º</p>
        </div>
        {product?.options?.map((option, index) => {
            return (
                <div key={index} style={{flexDirection: 'row', display: 'flex'}}>
                    <p>{option?.name}: </p>
                    {product?.options[index]?.values?.map((value, i) => {
                        return (
                            <div key={i} style={{flexDirection: 'row', display: 'flex',  marginLeft: 5, width: 'auto', justifyContent: 'center'}}>
                                <p style={{fontSize: '13px', border: '1px solid', borderRadius: 10, justifyContent: 'center', alignItems: 'center', display: 'flex', paddingLeft: 2, paddingRight: 2}}>{value}</p>
                            </div>
                        )
                    })}
                </div>
            )
        })}
        <div onClick={toggleModal} style={{display: 'flex', justifyContent: 'center', backgroundColor: 'red', width: '40%', height: '10%', borderRadius: '10px', cursor: 'pointer'}}>
            <p style={{textAlign: 'center'}}>Daha Fazla</p>
        </div>

        <Modal
            isOpen={isOpen}
            onRequestClose={toggleModal}
            contentLabel="My dialog"
        >
        <div>My modal dialog.</div>
        <button onClick={toggleModal}>Close modal</button>
      </Modal>
    </div>
  )
}

export default Description
