import './App.css';
import React, { useState, useEffect } from 'react';
import Product from './product'

const App = () => {
  const [input, setInput] = useState("")
  const [error, setError] = useState(null)
  const [products, setProducts] = useState([])

  useEffect(() => {
    const obj = {  
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'x-access-token': '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',
      },
    }
    fetch("https://teknasyon.netlify.app/.netlify/functions/products", obj)
      .then(res => res.json())
      .then(
        (result) => {
          console.log(result)
          setProducts(result?.products)
        },
        (error) => {
          setError(error);
        }
      )
  }, [])

  const handleChange = event => {
    setInput(event.target.value);
    let filter = event.target.value.toUpperCase();
    const newProductArray = products?.filter(product => {
      const itemData = product?.name
            ? product?.name.toUpperCase()
            : ''.toUpperCase();
      return itemData.indexOf(filter) > -1
    })
    setProducts(newProductArray);
  }

  return (
    <div style={{backgroundColor: 'white', textAlign: 'center'}}>
      <textarea value={input} onChange={handleChange} />
      {products?.map(product => {
        return (
          <Product product={product} />
        )
      })}
    </div>
  );
}

export default App;
