import './App.css';
import React, { useState, useEffect } from 'react';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';
import Product from './product'

const App = () => {
  const [input, setInput] = useState("")
  const [error, setError] = useState(null)
  const [products, setProducts] = useState([])
  const [filteredDataSource, setFilteredDataSource] = useState([]);

  useEffect(() => {
    const obj = {  
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'x-access-token': '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',
      },
    }
    fetch("https://teknasyon.netlify.app/.netlify/functions/products", obj)
      .then(res => res.json())
      .then(
        (result) => {
          console.log(result)
          setProducts(result?.products)
          setFilteredDataSource(result?.products)
        },
        (error) => {
          setError(error);
        }
      )
  }, [])

  const handleChange = event => {
    if (event.target.value) {
      const newData = products?.filter(product => {
          const itemData = product?.title
              ? product?.title?.toUpperCase()
              : ''.toUpperCase();
          const textData = event.target.value.toUpperCase();
          return itemData.indexOf(textData) > -1;
        }
      );
      setFilteredDataSource(newData);
    } else {
      setFilteredDataSource(products);
    }

    setInput(event.target.value);
  }

  return (
    <div style={{backgroundColor: 'white', textAlign: 'center'}}>
      <textarea value={input} onChange={handleChange} />
      {!filteredDataSource?.length && <p>Ürün Bulunamadı</p>}
      <div style={{flexDirection: 'row', display: 'flex', flexWrap: 'wrap', justifyContent: 'space-evenly'}}>
        {filteredDataSource?.map(product => {
          return (
            <div style={{marginLeft: 10, width: '20%', marginTop: 10}}>
              <Product product={product} />
            </div>
          )
        })}
      </div>
      <Popup
    trigger={<button className="button"> Open Modal </button>}
    modal
    nested
  >
    {close => (
      <div className="modal">
        <button className="close" onClick={close}>
          &times;
        </button>
        <div className="header"> Modal Title </div>
        <div className="content">
          {' '}
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Atque, a nostrum.
          Dolorem, repellat quidem ut, minima sint vel eveniet quibusdam voluptates
          delectus doloremque, explicabo tempore dicta adipisci fugit amet dignissimos?
          <br />
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Consequatur sit
          commodi beatae optio voluptatum sed eius cumque, delectus saepe repudiandae
          explicabo nemo nam libero ad, doloribus, voluptas rem alias. Vitae?
        </div>
        <div className="actions">
          <Popup
            trigger={<button className="button"> Trigger </button>}
            position="top center"
            nested
          >
            <span>
              Lorem ipsum dolor sit amet, consectetur adipisicing elit. Beatae
              magni omnis delectus nemo, maxime molestiae dolorem numquam
              mollitia, voluptate ea, accusamus excepturi deleniti ratione
              sapiente! Laudantium, aperiam doloribus. Odit, aut.
            </span>
          </Popup>
          <button
            className="button"
            onClick={() => {
              console.log('modal closed ');
              close();
            }}
          >
            close modal
          </button>
        </div>
      </div>
    )}
  </Popup>
    </div>
  );
}

export default App;
