import './App.css';
import React, { useState, useEffect } from 'react';
import Product from './product'

const App = () => {
  const [input, setInput] = useState("")
  const [error, setError] = useState(null)
  const [products, setProducts] = useState([])
  const [filteredDataSource, setFilteredDataSource] = useState([]);

  useEffect(() => {
    const obj = {  
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'x-access-token': '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',
      },
    }
    fetch("https://teknasyon.netlify.app/.netlify/functions/products", obj)
      .then(res => res.json())
      .then(
        (result) => {
          console.log(result)
          setProducts(result?.products)
          setFilteredDataSource(result?.products)
        },
        (error) => {
          setError(error);
        }
      )
  }, [])

  const handleChange = event => {
    if (event.target.value) {
      const newData = products?.filter(product => {
          const itemData = product?.title
              ? product?.title?.toUpperCase()
              : ''.toUpperCase();
          const textData = event.target.value.toUpperCase();
          return itemData.indexOf(textData) > -1;
        }
      );
      setFilteredDataSource(newData);
    } else {
      setFilteredDataSource(products);
    }

    setInput(event.target.value);
  }

  return (
    <div style={{backgroundColor: 'white', textAlign: 'center'}}>
      <textarea value={input} onChange={handleChange} />
      {!filteredDataSource?.length && <p>Ürün Bulunamadı</p>}
      <div style={{flexDirection: 'row', display: 'flex', flexWrap: 'wrap'}}>
        {filteredDataSource?.map(product => {
          return (
            <div style={{marginLeft: 10, width: '50%'}}>
              <Product product={product} />
            </div>
          )
        })}
      </div>
    </div>
  );
}

export default App;
