import './App.css';
import React, { useState, useEffect } from 'react';
import Product from './product'

const App = () => {
  const [input, setInput] = useState("")
  const [error, setError] = useState(null)
  const [products, setProducts] = useState([])
  const [filteredDataSource, setFilteredDataSource] = useState([]);
  const [pageNumber, setPageNumber] = useState(null);
  const [selectedPage, setSelectedPage] = useState(0);
  const [currentProducts, setCurrentProducts] = useState([]);
  const [selectedData, setSelectedData] = useState([]);

  const itemNumberPerPage = 10;

  useEffect(() => {
    const obj = {  
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'x-access-token': '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',
      },
    }
    fetch("https://teknasyon.netlify.app/.netlify/functions/products", obj)
      .then(res => res.json())
      .then(
        (result) => {
          console.log(result)
          setProducts(result?.products)
          setFilteredDataSource(result?.products)
          setPageNumber(Math.ceil(result?.products?.length / itemNumberPerPage))
          setCurrentProducts(result?.products?.slice(selectedPage * itemNumberPerPage, ((selectedPage + 1) * itemNumberPerPage)))
          setSelectedData(result?.products?.slice(selectedPage * itemNumberPerPage, ((selectedPage + 1) * itemNumberPerPage)))
        },
        (error) => {
          setError(error);
        }
      )
  }, [])

  useEffect(() => {
    setCurrentProducts(filteredDataSource.slice(selectedPage * itemNumberPerPage, ((selectedPage + 1) * itemNumberPerPage)))
  }, [selectedPage])

  const handleChange = event => {
    if (event.target.value) {
      const newData = products?.filter(product => {
          const itemData = product?.title
              ? product?.title?.toUpperCase()
              : ''.toUpperCase();
          const textData = event.target.value.toUpperCase();
          return itemData.indexOf(textData) > -1;
        }
      );
      setFilteredDataSource(newData);
      setSelectedData(newData)
    } else {
      setFilteredDataSource(products);
      setSelectedData(currentProducts)
    }

    setInput(event.target.value);
  }

  return (
    <div style={{backgroundColor: 'white'}}>
      <div style={{flexDirection: 'row', display: 'flex', width: '91%', marginLeft: 60, marginTop: 10, justifyContent: 'space-evenly'}}>
        <p>Ürün Ara: </p>
        <textarea style={{borderRadius: 10, marginTop: 10, alignItems: 'center', justifyContent: 'center', width: '91%', height: '10%', fontSize: 13}} value={input} onChange={handleChange} />
      </div>
      {!selectedData?.length && <p style={{textAlign: 'center'}}>Ürün Bulunamadı</p>}
      <div style={{flexDirection: 'row', display: 'flex', flexWrap: 'wrap', marginLeft: 60}}>
        {selectedData?.map(product => {
          return (
            <div style={{marginLeft: 20, width: '20%', marginTop: 10}}>
              <Product product={product} />
            </div>
          )
        })}
      </div>
      <div style={{flexDirection: 'row', display: 'flex', justifyContent: 'center'}}>
        {Array.from({length: pageNumber}, (item, index) => {
          return (
            <div style={{cursor: 'pointer'}} onClick={() => {
              setSelectedPage(index);
            }}>
              <p style={{textAlign: 'center', marginLeft: 10, border: '1px solid blue', borderRadius: '5px', padding: '5px'}}>{index + 1}</p>
            </div>
          )
        })}
      </div>
    </div>
  );
}

export default App;
