import React, {useState, useEffect} from 'react'
import Modal from "react-modal";

const Description = ({ product }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [images, setImages] = useState([]);
  const [quantity, setQuantity] = useState(1);

  const toggleModal = () => {
    setIsOpen(!isOpen);
  }

  useEffect(() => {
      let images = [];
      for (let i = 0; i < product?.images?.length; i++) {
        images.push(product?.images[i]?.src)
      }
      setImages(images);
  }, [])

  const incrementQuantity = () => {
    setQuantity(quantity + 1);
  }

  const decrementQuantity = () => {
    quantity && setQuantity(quantity - 1);
  } 

  return (
    <div style={styles.main}>
        <h3 style={styles.colorWhite}>{product?.title}</h3>
        <p style={styles.colorWhite}>{product?.variants[0]?.price}₺</p>
        <div onClick={toggleModal} style={styles.openDetailButton}>
            <p style={styles.openDetailText}>Ürün Detayı</p>
        </div>

        <Modal isOpen={isOpen} onRequestClose={toggleModal}>
            <div style={styles.modal}>
                <p onClick={toggleModal} style={{position: 'absolute', top: 0, right: 15, cursor: 'pointer'}}>Kapat</p>
                <div style={{display: 'flex', width: '30%', flexDirection: 'column', justifyContent: 'space-evenly', marginLeft: '5%'}}>
                    {images?.map(image => <img src={image} style={{width: '100%', height: '40%', borderRadius: '10px', marginTop: 10, marginBottom: 10, border: '1px solid black'}} /> )}
                </div>
                <div style={{display: 'flex', flexDirection: 'column', justifyContent: 'space-evenly', marginLeft: '15%', width: '40%'}}>
                    <h1 style={{color: 'red'}}>{product?.title}</h1>
                    <div style={{display: 'flex', flexDirection: 'row', alignItems: 'center'}}>
                        <h4>Satıcı: </h4>
                        <p style={{marginLeft: '2%'}}>{product?.vendor}</p>
                    </div>
                    <div style={{display: 'flex', flexDirection: 'row', alignItems: 'center'}}>
                        <h4>Stok Durumu: </h4>
                        <p style={{color: product?.status ? 'green' : 'red', marginLeft: '2%'}}>{product?.status ? 'Stokta Mevcut' : 'Stokta Bulunmuyor'}</p>
                    </div>
                    {product?.options?.map((option, index) => {
                        return (
                            <div key={index} style={{flexDirection: 'row', display: 'flex'}}>
                                <h4>{option?.name}: </h4>
                                {product?.options[index]?.values?.map((value, i) => {
                                    return (
                                        <div key={i} style={{flexDirection: 'row', display: 'flex',  marginLeft: '2%', width: 'auto', justifyContent: 'center'}}>
                                            <p style={{fontSize: '13px', border: '1px solid', borderRadius: 10, justifyContent: 'center', alignItems: 'center', display: 'flex', paddingLeft: 2, paddingRight: 2}}>{value}</p>
                                        </div>
                                    )
                                })}
                            </div>
                        )
                    })}
                    <div style={{display: 'flex', flexDirection: 'row', alignItems: 'center'}}>
                        <h4 style={{marginRight: '2%'}}>Ürün Adeti:</h4>
                        <div onClick={decrementQuantity} style={{width: '10%', height: 'auto', backgroundColor: '#d9d9d9', borderTopLeftRadius: 10, borderBottomLeftRadius: 10, alignItems: 'center', display: 'flex', justifyContent: 'center', cursor: 'pointer'}}>
                            <p>-</p>
                        </div>
                        <div style={{width: '10%', height: 'auto', backgroundColor: '#5a4844', alignItems: 'center', display: 'flex', justifyContent: 'center'}}>
                            <p style={{marginLeft: '2%', marginRight: '2%', color: 'white'}}>{quantity}</p>
                        </div>
                        <div onClick={incrementQuantity} style={{width: '10%', height: 'auto', backgroundColor: '#d9d9d9', borderTopRightRadius: 10, borderBottomRightRadius: 10, alignItems: 'center', display: 'flex', justifyContent: 'center', cursor: 'pointer'}}>
                            <p>+</p>
                        </div>
                    </div>
                    <div style={{flexDirection: 'row', display: 'flex'}}>
                        <div onClick={() => !quantity && alert('Ürün Adedi Belirleyiniz')} style={{display: 'flex', justifyContent: 'center', alignItems: 'center', backgroundColor: 'green', width: '40%', height: '75%', borderRadius: '10px', cursor: 'pointer'}}>
                            <p style={{textAlign: 'center', color: 'white'}}>Sepete Ekle</p>
                        </div>
                        <div style={{display: 'flex', justifyContent: 'center', alignItems: 'center', backgroundColor: 'gray', width: '40%', height: '75%', borderRadius: '10px', cursor: 'pointer', marginLeft: '5%'}}>
                            <p style={{textAlign: 'center', color: 'white'}}>İstek Listesine Ekle</p>
                        </div>
                    </div>
                </div>
            </div>
      </Modal>
    </div>
  )
}

const styles = {
    main: {display: 'flex', justifyContent: 'space-between', flex: 1, flexDirection: 'column'},
    colorWhite: {color: 'white'},
    openDetailButton: {display: 'flex', justifyContent: 'center', backgroundColor: 'red', width: '40%', height: '10%', borderRadius: '10px', cursor: 'pointer'},
    openDetailText: {textAlign: 'center', color: 'white'},
    modal: {display: 'flex', flexDirection: 'row', height: '100%', alignSelf: 'center'},
}

export default Description
