import './App.css';
import React, { useState } from 'react';
import Product from './Product'

const App = () => {
  const [input, setInput] = useState("");

  const handleChange = event => {
    setInput(event.target.value)
  }
  return (
    <div className="App">
      <textarea value={input} onChange={handleChange} />
      <p>{input}</p>
      <Product/>
    </div>
  );
}

export default App;
