import React, {useState} from 'react'
const Description = ({ product }) => {

  const [price, setPrice] = useState(30)

  return (
    <div style={{marginLeft: 10, display: 'flex', flexDirection: 'column', justifyContent: 'space-evenly'}}>
      <p>{product?.title}</p>
      <p style={{color: product?.status ? 'green' : 'red'}}>{product?.status ? 'Stokta Mevcut' : 'Stokta Bulunmuyor'}</p>
      {product?.options?.map((option, index) => {
        return (
            <div key={index} style={{flexDirection: 'row', display: 'flex', marginTop: 10}}>
                <p>{option?.name}: </p>
                {product?.options[index]?.values?.map((value, i) => {
                    return (
                        <div key={i} style={{flexDirection: 'row', display: 'flex', border: '1px solid', borderRadius: 10, marginLeft: 10, width: 'auto', justifyContent: 'center', paddingLeft: 5, paddingRight: 5}}>
                            <p>{value}</p>
                        </div>
                    )
                })}
            </div>
        )
      })}
      <p>{price}</p>
    </div>
  )
}

export default Description
