import React from 'react'
import Description from './description'

const index = ({ product }) => {
  return (
    <div key={product?.id} style={{width: '80%', padding: '5px', border: '1px solid black', marginBottom: '10px', textAlign: 'left', display: 'flex', flexDirection: 'row'}}>
      <div style={{height: '100%', backgroundColor: 'red', display: 'flex', justifyContent: 'center'}}>
      <img src={product?.image?.src} style={{width: '13%', height: '10%', border: '1.5px solid black', borderRadius: '10px', padding: 5}} />
      </div>
      <Description product={product} />
    </div>
  )
}

export default index
